  let B := -2 * a * b;
  let C := b^2;
  B^2 - 4 * A * C = 0 :=
begin
  let A := a^2,
  let B := -2 * a * b,
  let C := b^2,
  calc
    B^2 - 4 * A * C = (-2 * a * b)^2 - 4 * (a^2) * (b^2) : by rw [A, B, C]
    ... = 4 * a^2 * b^2 - 4 * a^2 * b^2 : by ring
    ... = 0 : by ring
end
