import Mathlib
import Aesop

set_option maxHeartbeats 0

open BigOperators Real Nat Topology Rat

theorem candies_left : 
  let first_jar := 75;
  let second_jar := 120;
  let eaten_from_first_jar := 35;
  let eaten_from_second_jar := 70;
  let remaining_in_first_jar := first_jar - eaten_from_first_jar;
  let remaining_in_second_jar := second_jar - eaten_from_second_jar;
  remaining_in_first_jar + remaining_in_second_jar = 90 :=
by
  calc
    _ = (75 - 35) + (120 - 70) := by rfl
    _ = 40 + 50 := by rfl
    _ = 90 := by rfl
