theorem rectangle_perimeter (A B C D : ℤ) (h1 : A = -4) (h2 : B = -1) (h3 : C = 3) (h4 : D = 6) :
  let AB := B - A;
  let CD := D - C;
  let BC := C - B;
  let AD := D - A;
  AB + CD + BC + AD = 14 :=
begin
  let AB := B - A,
  let CD := D - C,
  let BC := C - B,
  let AD := D - A,
  have hAB : AB = 3 := by rw [h2, h1]; norm_num,
  have hCD : CD = 3 := by rw [h4, h3]; norm_num,
  have hBC : BC = 4 := by rw [h3, h2]; norm_num,
  have hAD : AD = 4 := by rw [h4, h1]; norm_num,
  rw [hAB, hCD, hBC, hAD],
  norm_num,
end
