import Mathlib
import Aesop

set_option maxHeartbeats 0

open BigOperators Real Nat Topology Rat

theorem total_candies_left : 
  let first_jar := 75;
  let second_jar := 120;
  let candies_eaten_from_first_jar := 35;
  let candies_eaten_from_second_jar := 70;
  let candies_left_in_first_jar := first_jar - candies_eaten_from_first_jar;
  let candies_left_in_second_jar := second_jar - candies_eaten_from_second_jar;
  let total_candies_left := candies_left_in_first_jar + candies_left_in_second_jar;
  total_candies_left = 90 :=
by
  calc
    _ = (75 - 35) + (120 - 70) := rfl
    _ = 40 + 50 := rfl
    _ = 90 := rfl
