import Mathlib
import Aesop

set_option maxHeartbeats 0

open BigOperators Real Nat Topology Rat

theorem rectangle_area_half_triangle_area (a b : ℝ) : 
  let AB := a;
  let BC := b;
  let AC := Real.sqrt (AB^2 + BC^2);
  let E := (AB + BC, 0);
  let ABE_area := (1/2) * AB * BC;
  let rectangle_area := AB * BC;
  ABE_area = (1/2) * rectangle_area := by
  simp [AB, BC, AC, E, ABE_area, rectangle_area]
  ring
