-- Define the slope of a line
def slope (A B : ℝ × ℝ) : ℝ := (B.2 - A.2) / (B.1 - A.1)

-- Define the distance between two points
def distance (A B : ℝ × ℝ) : ℝ := Real.sqrt ((B.1 - A.1)^2 + (B.2 - A.2)^2)

-- Define the perpendicular distance between two parallel lines
def perp_distance (A B : ℝ × ℝ) (m : ℝ) : ℝ := abs (A.2 - B.2 - m * (A.1 - B.1)) / Real.sqrt (1 + m^2)

-- Define the length of a line segment
def line_length (A B : ℝ × ℝ) : ℝ := distance A B

-- Define the angle between two lines
def angle_between_lines (m1 m2 : ℝ) : ℝ := Real.atan (abs ((m1 - m2) / (1 + m1 * m2)))

-- Define the length of a line intersecting two parallel lines
def length_of_intersecting_line (d m1 m2 m3 : ℝ) : ℝ := d / Real.sin (angle_between_lines m1 m2)

