import Mathlib
import Aesop

set_option maxHeartbeats 0

open BigOperators Real Nat Topology Rat

theorem remaining_candies : 
  let firstJar := 75;
  let secondJar := 120;
  let removedFromFirstJar := 35;
  let removedFromSecondJar := 70;
  let remainingInFirstJar := firstJar - removedFromFirstJar;
  let remainingInSecondJar := secondJar - removedFromSecondJar;
  let totalRemainingCandies := remainingInFirstJar + remainingInSecondJar;
  totalRemainingCandies = 90 :=
by
  calc
    _ = 75 - 35 + (120 - 70) := by rfl
    _ = 40 + 50 := by rfl
    _ = 90 := by rfl
