import Mathlib
import Aesop

set_option maxHeartbeats 0

open BigOperators Real Nat Topology Rat

theorem johns_apples : 
  let friends := 5;
  let apples_per_friend := 2;
  let remaining_apples := 7;
  let total_given_away := friends * apples_per_friend;
  let original_apples := total_given_away + remaining_apples;
  original_apples = 17 :=
by
  calc
    _ = 5 * 2 + 7 := by rfl
    _ = 10 + 7 := by rfl
    _ = 17 := by rfl
